    return(
        <Stack
            spacing={4}
            w={{ base: "80%", md: "40%" }}
            align={["center", "center"]}
        >
            <Heading
                as="h1"
                size="xl"
                fontWeight="bold"
                color="primary.800"
                textAlign={["center"]}
            >
                Cross-Chain Reputation
            </Heading>

            <Heading
                as="h2"
                size="md"
                color="primary.800"
                opacity="0.8"
                fontWeight="normal"
                lineHeight={1.5}
                textAlign={["center"]}
            >
                Let's analyze your history in Everscale and compute Reputation!
            </Heading>

            <Button
                borderRadius="8px"
                py="4"
                px="4"
                lineHeight="1"
                size="md"
                onClick={
                    fetchUserData
                }
            >
                Compute
            </Button>

            <Text
                fontSize="xs"
                mt={2}
                textAlign="center"
                color="primary.800"
                opacity="0.6"
            >
                It takes about 20 seconds.
            </Text>
        </Stack>
    );
}

export default Body;
