import { Button, Heading, Stack, Text } from "@chakra-ui/react";
import axios from "axios";
import retry from "retry";

function Body() {

    async function fetchUserData() {
        axios({
          method: 'post',
          url: '/user/12345',
          data: {
            firstName: 'Fred',
            lastName: 'Flintstone'
          }
        }).then(function (response) {
            response.data
        });

        // const requestOptions = {
        //     method: "POST",
        //     headers: { 
        //         'Content-Type': 'application/json'
        //     },
        //     body: JSON.stringify({ 
        //         userAddress: "0:ca17121b6e4d5d3a9aa019b24bbe8922974b64e974a8e2dcdb3134ddc6737715",
        //         limit: 10,
        //         offset: 10
        //     })
        // };

        // fetch("https://api.flatqube.io/v1/transactions", requestOptions)
        //     .then(response => console.log(response.data))
        //     .catch(error => console.log(error))

    }

    return(
        <Stack
            spacing={4}
            w={{ base: "80%", md: "40%" }}
            align={["center", "center"]}
        >
            <Heading
                as="h1"
                size="xl"
                fontWeight="bold"
                color="primary.800"
                textAlign={["center"]}
            >
                Cross-Chain Reputation
            </Heading>

            <Heading
                as="h2"
                size="md"
                color="primary.800"
                opacity="0.8"
                fontWeight="normal"
                lineHeight={1.5}
                textAlign={["center"]}
            >
                Let's analyze your history in Everscale and compute Reputation!
            </Heading>

            <Button
                borderRadius="8px"
                py="4"
                px="4"
                lineHeight="1"
                size="md"
                onClick={
                    fetchUserData
                }
            >
                Compute
            </Button>

            <Text
                fontSize="xs"
                mt={2}
                textAlign="center"
                color="primary.800"
                opacity="0.6"
            >
                It takes about 20 seconds.
            </Text>
        </Stack>
    );
}

export default Body;
